import { LitElement, html, css } from 'lit-element';

/**
 * `div-hexagon`
 * DivHexagon
 *
 * @customElement div-hexagon
 * @polymer
 * @litElement
 * @demo demo/index.html
 */

class DivHexagon extends LitElement {
  static get is() {
    return 'div-hexagon';
  }

  static get properties() {
    return {
      title: { type: String },
      description: { type: String },
      img: { type: String }
    };
  }

  static get styles() {
    return css`
      :host {
        display: block;
        /* hexagon */
        --gap: 2vmin;
        --radio: calc(100vmin / 4);
        --width: calc(var(--radio) * .866);
        --height: var(--radio);
        /* colors */
        --negroC: #444448;
        --blancoS: #e1e8ed;

        --main-color: var(--blancoS);
        --main-bg: var(--negroC);
      }
      article {
        color: var(--main-color);
        background: var(--main-bg);
        width: var(--width); 
        height: var(--height);
        overflow: hidden;
        transition: 1s;
        clip-path: url(#hexa-v);
        clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
        filter: drop-shadow(0 0 1px #fff);
      }
      figure {
        display: flex;
        flex-direction: column;
        flex-wrap: nowrap;
        justify-content: center;
        height: var(--height);
        font-size: calc(1rem + 1vmin);
        line-height: 1.2;
        transition: .75s .05s;
        hyphens: auto;
        text-align: center;
      }
      img {
        display:none;
        background: #fff;
        min-width: var(--radio);
        min-height: var(--radio);
        max-width: var(--width);
        max-height: var(--height);
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        transform-origin: 50% 0%;
        transition: 1s;
        clip-path: url(#hexagono);
        clip-path: inherit;
        z-index:  10;
      }
      article:hover img {
        transform: translate(-50%, -50%) rotateY(.25turn);
      }
      h2 {
        font-size: 100%;
        font-weight: 200;
        font-variant: small-caps;
        padding: 0;
      }
      figure p {
        font-size: 70%;
        line-height: 1.2;
        font-weight: 100;
        width: 100%;
      }
      a {
        display: block;
        color: var(--gris);
        text-decoration: none;
      }
      a:hover {
        color: var(--blancoS);
      }
      @media only screen and (max-width: 450px) {
        figure p {display: none;}
        figure h2 {font-size: 1rem;}
      }
    `;
  }

  constructor() {
    super();
    this.title = 'TITULO';
    this.description = 'Loren ipsum y no se qué mas en latín pero si añado mas lo mismo se va el espacio...';
    this.img = 'data:image/png;base64,'
  }

  render() {
    return html`
      <article>
        <figure>
          <h2>${this.title}</h2>
          <p>${this.description}</p>
        </figure>
        <img src="${this.img}" />
      </article>
    `;
  }
}

window.customElements.define(DivHexagon.is, DivHexagon);
export { DivHexagon as default };